mapper.select:
select a.company_id,a.n1,a.n2,a.sex,a.age,a.ID,a.phone,a.code,a.check_num,g.time from (select p.company_id,p.name n1,c.name n2,p.sex,p.age,p.ID,p.phone,p.code,p.check_num from tb_patient p left join tb_company c on p.company_id = c.company_id)a left join tb_group g on a.company_id = g.company_id where 1 = 1
and a.n1 like '%${name}%'
and a.phone = #{phone}
and g.time = #{time}
and a.code like '%${code}%'

select a.company_id,a.n1,a.n2,a.sex,a.age,a.ID,a.phone,a.code,a.check_num,a.account,g.time from (select p.company_id,p.name n1,c.name n2,p.sex,p.age,p.ID,p.phone,p.code,p.check_num,c.account from tb_patient p left join tb_company c on p.company_id = c.company_id)a left join tb_group g on a.company_id = g.company_id where 1 = 1
and a.n1 like '%${name}%'
and g.time = #{time}
and a.account = #{account}

select a3.n1,a3.n2,a3.time,par.`name` n3 from (select a2.n1,a2.n2,a2.param_id,gro.time from (select a1.name n1,pro.`name` n2,a1.project_id,pro.param_id,a1.company_id from (select pp.patient_id,pp.project_id,pat.`name`,pat.company_id from tb_patient_project pp left join tb_patient pat on pp.patient_id = pat.patient_id) a1 left join tb_project pro on a1.project_id = pro.project_id) a2 left join tb_group gro on a2.company_id = gro.company_id) a3 left join tb_param par on a3.param_id = par.param_id where 1 = 1
and a3.n1 like '%${name}%'
and a3.time = #{time}

select a8.n1,a8.n2,a8.n3,a8.n4,a8.unit,a8.min,a8.max,a8.resulttext,a8.time from(select a7.n1,a7.n2,a7.n3,com.`name` n4,a7.unit,a7.min,a7.max,a7.resulttext,a7.time,a7.user_id from(select a6.n1,a6.n2,a6.n3,pc.combo_id,a6.unit,a6.min,a6.max,a6.resulttext,a6.time,a6.user_id from(select a5.n1,a5.n2,pro.`name` n3,a5.unit,a5.min,a5.max,a5.resulttext,a5.time,a5.patient_id,a5.user_id from (select a4.n1,a4.n2,a4.unit,a4.min,a4.max,a4.resulttext,gro.time,a4.patient_id,a4.project_id,a4.user_id from (select a3.n1,a3.n2,a3.unit,a3.min,a3.max,br.resulttext,a3.company_id,a3.patient_id,a3.project_id,a3.user_id from (select a2.`name` n1,det.`name` n2,det.unit,det.min,det.max,a2.detail_id,a2.company_id,a2.patient_id,a2.project_id,a2.user_id from(select a1.`name`,dp.detail_id,a1.company_id,a1.patient_id,a1.project_id,a1.user_id from (select pat.`name`,pp.project_id,pat.company_id,pat.patient_id,pp.user_id from tb_patient_project pp left join tb_patient pat on pp.patient_id = pat.patient_id) a1 left join tb_detail_project dp on a1.project_id = dp.project_id) a2 left join tb_detail det on a2.detail_id = det.detail_id) a3 left join tb_brief br on a3.detail_id = br.detail_id) a4 left join tb_group gro on a4.company_id = gro.company_id) a5 left join tb_project pro on a5.project_id = pro.project_id) a6 left join tb_patient_combo pc on a6.patient_id = pc.patient_id) a7 left join tb_combo com on a7.combo_id = com.combo_id) a8 left join tb_user us on a8.user_id = us.user_id where 1 = 1
and a8.n1 like '%${name}%'
and a8.time = #{time}

select a1.n1,a1.time,a1.detail_id,sum.advise,sum.guide from(select pat.name n1,bri.summarize_id,bri.time,bri.detail_id from tb_brief bri left join tb_patient pat on pat.patient_id = bri.patient_id) a1 left join tb_summarize sum on a1.summarize_id = sum.summarize_id where 1 = 1
and a1.n1 like '%${name}%'
and a1.time = #{time}
